sanctum[sanctum.length] = '<div class="title">A Late Arrival</div><div class="carbon">The Inner Sanctum</div><div class="title">1</div><div><span class="flavour">The door to the chapel opens, letting driving wind and rain into the room. A robed figure enters, carrying an unconscious woman over his shoulder.</span> <span class="text">The keeper places a cult leader in the chapel. If an investigator kills this monster, he gains 1 skill point and the "Cult Robes" exploration card.</span></div>'
sanctum[sanctum.length] = '<div class="title">Dark Powers</div><div class="carbon">Dark Powers</div><div class="title">2</div><div><span class="flavour">Deep within the monastery, an ancient ceremony begins. Blood is spilled and a dark power closes its grasp on your soul.</span> <span class="text">The keeper gains 1 threat for each cultist and cult leader on the game board.</span></div>'
sanctum[sanctum.length] = '<div class="title">Urgency</div><div class="carbon">The Inner Sanctum</div><div class="title">3</div><div><span class="flavour">A sense of urgency fills the monastery. Time is running out, and the burning feeling is almost tangible.</span> <span class="text">The keeper may move each cultist 1 space. Each investigator may then move 1 space.</span></div>'
